import React, { useState } from "react";
import SignUpContext from "./SignUpContext";

const SignUpState = (props) => {
  const [signUpShow, setSignUpShow] = useState(false);
  const [isShowLogin, setIsShowLogin] = useState(false);
  const [isShowUpload, setIsShowUpload] = useState(false)

  const handleLoginClick = () => {
      setIsShowLogin((isShowLogin)=> !isShowLogin)
  }

  const handleRegisterClick = () => {
      setSignUpShow((signUpShow)=> !signUpShow)
  }

  const handleUploadClick = () => {
    setIsShowUpload((signUpShow)=> !signUpShow)
}
  return (
    <SignUpContext.Provider value={{ signUpShow, setSignUpShow, isShowLogin, setIsShowLogin, handleLoginClick, handleRegisterClick }}>
      {props.children}
    </SignUpContext.Provider>
  );
};

export default SignUpState;
