import React, { useState } from "react";
import SignUpContext from "./SignUpContext";
import axios from "axios";

const SignUpState = (props) => {
  const [isShowLogin, setIsShowLogin] = useState(false);
  const [isShowUpload, setIsShowUpload] = useState(false);
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [result, setResult] = useState(null);
  const [isRegistered, SetIsRegistered] = useState(false);



  const log = () => {
    const loginSession = JSON.parse(sessionStorage.getItem("login")) || {
      username: "",
      loggedIn: false,
      };

      const [username, setUsername] = useState(loginSession["username"]);
      const [loggedIn, setLoggedIn] = useState(loginSession["loggedIn"]);

      const handleLogin = (_username) => {
      if (_username) {
      setUsername(_username);
      setLoggedIn(true);
      } else {
      setUsername("");
      setLoggedIn(false);
      }
      }; 
  };





  const [newUser, setNewUser] = useState({
    firstName: "-",
    lastName: "",
    email: "",
    street: "",
    city: "",
    postcode: "",
    country: "",
    birthday: "",
    password: "",
  });

  const logoutHandler = async () => {
    setIsAuthenticated(false);
    const response = await axios.get("http://localhost:3001/user/logout", {
      withCredentials: true,
    });
    setResult(response.data);
  };

  const handleLoginClick = async () => {
    if (isAuthenticated) {
      setIsAuthenticated(false);
      const response = await axios.get("http://localhost:3001/user/logout", {
        withCredentials: true,
      });
      setResult(response.data);
    } else {
      setIsShowLogin((isShowLogin) => !isShowLogin);
    }
  };

  const handleUploadClick = () => {
    if (isAuthenticated) {
      setIsShowUpload((isShowUpload) => !isShowUpload);
    } else {
      return;
    }
  };
  return (
    <SignUpContext.Provider
      value={{
        isShowLogin,
        setIsShowLogin,
        handleLoginClick,
        handleUploadClick,
        isShowUpload,
        isAuthenticated,
        setIsAuthenticated,
        result,
        setResult,
        logoutHandler,
        newUser,
        setNewUser,
        isRegistered,
        SetIsRegistered,

      }}
    >
      {props.children}
    </SignUpContext.Provider>
  );
};

export default SignUpState;
