import React, { useContext, useState, use } from "react";
import axios from "axios";
import SignUpContext from "./context/SignUpContext";
import { NavLink } from "react-router-dom";
import Fotos from "./Fotos"

//Foto Upload

const Upload = () => {
  const { handleUploadClick, isShowUpload } = useContext(SignUpContext);

  const [file, setFile] = useState(null);
  const [title, setTitle] = useState("");
  const changeTitle = (e) => {
    setTitle(e.target.value);
  };
  const handleSubmit = async (e) => {
    e.preventDefault();
    const data = new FormData();
    data.append("avatar", file);
    data.append("title", title);

    const config = {
      headers: {
        Accept: "application/json",
        "Content-Type": "multipart/form-data",
      },
      withCredentials: true,
    };

    axios
      .post("http://localhost:3001/user/upload", data, config)
      .then((res) => console.log(res.data))
      .catch((err) => console.log(err));
  };

// get fotos

const [users, setUsers] = useState([]);
const [page ,setPage] = useState(1);


const getUsers = async(page =1 ,limit = 300)=>{
const result = await axios.get(`http://localhost:5000/api/user/paginate?page=${page}&limit=${limit}`);
if(result){
  setUsers(result.data.allUsers)
}
}
useEffect(()=>{
  console.log(' start');
  getUsers();
},[]);

useEffect(()=>{
  getUsers(page);
},[page]);

// to go to next page 
const next = ()=>{
 const nextPage = page +1;
 setPage(nextPage) 
}

// to go to previous page
const previous = ()=>{
 const previousPage = page -1 
 if(previousPage < 1){
   setPage(1)
 }
 else{
   setPage(previousPage)
 }
}
return (
<>
<div className='row container'>
  <div className='col-3'>
     <p>{ users.length} users found</p>
     <p> <b> page : {page} </b></p>
  </div>
  <div className='col'>
     <button onClick={previous} className='btn btn-info' >Previous</button> &nbsp;&nbsp;
     <button onClick={next} className='btn btn-info' disabled={users.length < 1} >next</button>
  </div>
</div>
<div className='flex-grid'>
   {
     users.length > 0 && users.map((item , index)=>{
       return (
         <div className='col' key = {index}>
           <Fotos user={item}   />
        </div>
       )
     })
   }
</div>
</>

)








  return (
    <div>
      <nav className="navbar navbar-expand-lg navbar-light bg-light">
        <div className="container-fluid">
          <div className="collapse navbar-collapse" id="navbarTogglerDemo02">
            <ul className="navbar-nav me-auto mb-2 mb-lg-0">
              <li className="nav-item">
                <a className="nav-link active" aria-current="page" href="#">
                  Foto Collection
                </a>
              </li>
              <li className="nav-item">
                <button
                  className="nav-link"
                  onClick={() => handleUploadClick()}
                >
                  Upload Foto
                </button>
              </li>
              <div className="dropdown">
                <button
                  className="btn btn-secondary dropdown-toggle"
                  type="button"
                  id="dropdownMenuButton1"
                  data-bs-toggle="dropdown"
                  aria-expanded="false"
                >
                  Filter
                </button>
                <ul
                  className="dropdown-menu"
                  aria-labelledby="dropdownMenuButton1"
                >
                  <li>
                    <a className="dropdown-item" href="#">
                      Action
                    </a>
                  </li>
                  <li>
                    <a className="dropdown-item" href="#">
                      Another action
                    </a>
                  </li>
                  <li>
                    <a className="dropdown-item" href="#">
                      Something else here
                    </a>
                  </li>
                </ul>
              </div>

              <div className="dropdown">
                <button
                  className="btn btn-secondary dropdown-toggle"
                  type="button"
                  id="dropdownMenuButton1"
                  data-bs-toggle="dropdown"
                  aria-expanded="false"
                >
                  Sort
                </button>
                <ul
                  className="dropdown-menu"
                  aria-labelledby="dropdownMenuButton1"
                >
                  <li>
                    <a className="dropdown-item" href="#">
                      Action
                    </a>
                  </li>
                  <li>
                    <a className="dropdown-item" href="#">
                      Another action
                    </a>
                  </li>
                  <li>
                    <a className="dropdown-item" href="#">
                      Something else here
                    </a>
                  </li>
                </ul>
              </div>
            </ul>
          </div>
        </div>
      </nav>

      <div className={`${isShowUpload ? "wrapper1 active" : "wrapper1 show"}`}>
        <form onSubmit={handleSubmit}>
          <div class="wrapper1">
            <div class="container1">
              <h1 className="upload-head">Upload a foto</h1>
              <div class="upload-container1">
                <div class="border-container1">
                  <div class="icons fa-4x">
                    <i
                      class="fas fa-file-image"
                      data-fa-transform="shrink-3 down-2 left-6 rotate--45"
                    ></i>
                    <i
                      class="fas fa-file-alt"
                      data-fa-transform="shrink-2 up-4"
                    ></i>
                    <i
                      class="fas fa-file-pdf"
                      data-fa-transform="shrink-3 down-2 right-6 rotate-45"
                    ></i>
                  </div>
                  <label htmlFor="title">Title</label>
                  <input type="text" name="title" onChange={changeTitle} />
                  <input
                    type="file"
                    id="file"
                    name="avatar"
                    onChange={(e) => {
                      const file = e.target.files[0];
                      setFile(file);
                      console.log(file);
                    }}
                  />
                  <input type="submit" className="warning" value="Upload" />
                  <p>Drag and drop foto here your computer.</p>
                </div>
              </div>
            </div>
          </div>
        </form>
      </div>
    </div>
  );
};

export default Upload;
